draw_boxes: 
	mov si, box
	mov dx, 0            
	mov bx, si
	add si, 2
	.for1:
		cmp dl, byte[bx+1]
		je .endfor1
		mov cx, 0        
	.for2:
		cmp cl, byte[bx]
		je .endfor2
		lodsb
		call box_positions
		inc cx
		jmp .for2
	.endfor2:
		inc dx
		jmp .for1
	.endfor1:
ret

